/**
 * 作者：Mr.wang
 * 时间：2022/4/11
 * 文件名：index
 * 项目名称：脚手架
 * WebStorm
 */
import React, {Component} from "react";
import footer from "./index..module.css"
export default class Footer extends Component {
    state = {
        isSelectAll:false
    }
    handleClick = ()=>{
        let {isSelectAll} = this.state
        this.setState({
            isSelectAll:!isSelectAll
        })
        if(!isSelectAll) this.props.selectAll()

    }
    render() {
        return (
            <div className={footer.main}>
                <input type="checkbox" checked={this.state.isSelectAll} onClick = {this.handleClick}/>
                <span>已完成5/共6</span>
                <button className={footer.btn}>清楚已完成任务</button>
            </div>
        );
    }
}
