/**
 * 作者：Mr.wang
 * 时间：2022/4/11
 * 文件名：index
 * 项目名称：脚手架
 * WebStorm
 */
import React, {Component} from "react";
import HeadInput from "../HeadInput";
import ListArea from "../ListArea";
import Footer from "../Footer";
import toDoList from "./index.module.css";

export default class ToDoList extends Component {
    state = {
        dataList:[
            {id:1,name:"吃饭",done:false},
            {id:2,name:"睡觉",done:false},
        ]
    }
    render() {
        return (
            <div className={toDoList.main}>
                <HeadInput addShowData = {this.addShowData}/>
                <ListArea showData={this.state.dataList} delItem={this.delItem} selectItem = {this.selectItem}/>
                <Footer selectAll = {this.selectAll} delDone={this.delDone} allLength = {this.getAllList()} allOk = {this.getOk()}/>
            </div>
        );
    }
    addShowData = (addData)=>{
            const {dataList} = this.state;
            this.setState({
                dataList: [addData,...dataList]
            })
    }
    selectItem = (id)=>{
        let dataList = this.state.dataList
        let index;
        let newDataList = dataList.map((item)=>{
            if (item.id == id){
                return 
            }
        })
        for (let i = 0;i<dataList.length; i++) {
            if (dataList[i].id === id) {
                index = i;
                break;
            }
        }
        dataList[index].done = !dataList[index].done;
        this.setState({dataList})
    }
    delItem = (id)=>{
        let dataList = this.state.dataList
        let index;
        for (let i = 0;i<dataList.length; i++) {
            if (dataList[i].id === id) {
                index = i;
                break;
            }
        }
        dataList.splice(index,1)
        this.setState({
            dataList
        })
    }
    selectAll = (tag)=>{
        let dataList = this.state.dataList
        for (let i = 0; i < dataList.length; i++) {
            dataList[i].done = tag;
        }
        this.setState({
            dataList
        })
    }
    delDone = ()=>{
        let dataList = this.state.dataList
        for (let i = 0; i < dataList.length; i++) {
            if (dataList[i].done) {
                dataList.splice(i,1)
                i--;
            }
        }
        this.setState({
            dataList
        })
    }
    getAllList = () => {
        return this.state.dataList.length
    }
    getOk = ()=>{
        let count = 0;
        let dataList = this.state.dataList
        for (let i = 0; i < dataList.length; i++) {
            if (dataList[i].done) {
                count ++
            }
        }
        return count;
    }
}
